package ejercicio1;

public class principal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 3, num2 = 5, num3 = 9, num4 = 1, resul, resul2;

		// caulculo resul:
		resul = num1 + num2 + num3;

		System.out.println("Hola, este programa realiza sumas");
		System.out.println("El resultado de la suma de los valores: 3,9 y 5 es igual a " + resul);
		// calculo resul2
		resul2 = resul - num4;

		System.out.println("\nSí a el resultado anterior le restamos 1 sería daria un total de " + resul2);

		System.out.println("Total=16");
		System.out.println("\nGracias por usar nuestro programa");

	}
}
